import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html'
})
export class FetchDataComponent {
  readonly APIUrl = 'https://localhost:7252/WeatherForecast'

  constructor(private http: HttpClient) {
    this.getItems();
  }

  public items: items[] = [];

  getItems() {
    this.http.get<items[]>(this.APIUrl).subscribe({
      next: (value) => {
        this.items = value;
      },
      error: (err) => {
        console.log(err);
      },
    });
  }

  public editedInfo: items = {
    ID: 0,
    FName: '',
    Username: '',
    Password: ''
  };

  openEditModal(item: items) {
    this.editedInfo = { ...item };
    console.log(this.editedInfo.Username);
  }

  openpostModal() {
    console.log('karakum: ' + this.editedInfo.FName);
  }

  Posting() {
    this.updating();
    this.http.post(`${this.APIUrl}`, this.editedInfo).subscribe({
      next: () => {
        console.log(`Posted: ${this.editedInfo.ID}`);
        this.getItems()
      },
      error: (err) => {
        console.log(err);
        console.log("error here!");
      },
    })
  }

  updating() {
    this.http.put(`${this.APIUrl}`, this.editedInfo).subscribe({
      next: () => {
        console.log(`Updated by ID: ${this.editedInfo.ID}`);
        this.getItems()
      },
      error: (err) => {
        console.log(err);
        console.log("error here!");
      },
    })
  }


  deleteInfo(ID: number) {
    this.http.delete(`${this.APIUrl}?ID=${ID}`).subscribe({
      next: () => {
        console.log(`Deleted by ID: ${ID}`);
        this.getItems()
      },
      error: (err) => {
        console.log(err);
      },
    })
  }

}


interface items {
  ID: number;
  FName: string;
  Username: string;
  Password: string;
}
