import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html'
})
export class FetchDataComponent {
  public items: items[] = [];

  constructor(http: HttpClient) {
    http.get<items[]>('https://localhost:7252/WeatherForecast').subscribe({
      next: (value) => {
        this.items = value;
        console.log(value);
      },
      error: (err) => {
        console.log(err);
      },
    });
  }
}

interface items {
  SName: string;
  SPrice: number;
  SDescription: string;
}
