import { Component, Inject } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html'
})
export class FetchDataComponent {
  readonly APIUrl = 'https://localhost:7252/WeatherForecast'

  constructor(private http: HttpClient) {
    this.getItems();
  }

  public items: items[] = [];

  getItems() {
    this.http.get<items[]>(this.APIUrl).subscribe({
      next: (value) => {
        this.items = value;
      },
      error: (err) => {
        console.log(err);
      },
    });
  }

  public editedShoe: items = {
    SName: '',
    SPrice: 0,
    SDescription: '',
  };


  openEditModal(event: any, item: any) {
    this.editedShoe = { ...item }; // Clone the item to editedShoe
  }

  updating() {
    this.http.put(`${this.APIUrl}?SName={this.item.SName}`, this.editedShoe).subscribe({
      next: () => {
        console.log(`Updated by Shoename: ${this.editedShoe.SName}`);
        this.getItems()
      },
      error: (err) => {
        console.log(err);
      },
    })
  }


  deleteShoe(SName: string) {

    this.http.delete(`${this.APIUrl}?SName=${SName}`).subscribe({
      next: () => {
        console.log(`Deleted by Shoename: ${SName}`);
        this.getItems()
      },
      error: (err) => {
        console.log(err);
      },
    })
  }

}


interface items {
  SName: string;
  SPrice: number;
  SDescription: string;
}
