import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { trigger, state, style, transition, animate } from '@angular/animations';

@Component({
  selector: 'app-counter-component',
  templateUrl: './counter.component.html',
  animations: [
    trigger('fadeInOut', [
      state('void', style({ opacity: 0 })),
      transition('* <=> *', [
        animate('300ms ease-in-out'),
      ]),
    ]),
  ],
})
export class CounterComponent implements OnInit {
  constructor(private http: HttpClient, private formBuilder: FormBuilder) {}

  readonly APIUrl = 'https://localhost:7252/WeatherForecast';

  public items: items[] = [];
  public infoForm!: FormGroup;
  public loginForm!: FormGroup;
  public isRegistration: boolean = true;

  ngOnInit() {
    this.infoForm = this.formBuilder.group({
      ID: [0],
      FName: ['', [Validators.required, Validators.maxLength(15)]],
      Username: ['', [Validators.required, Validators.maxLength(10)]],
      Password: [
        '',
        [
          Validators.required,
          Validators.maxLength(15),
          Validators.pattern('^(?=.*[A-Z])(?=.*\\d)[A-Za-z\\d]{8,}$')
        ]
      ]
    });

    this.loginForm = this.formBuilder.group({
      Username: ['', [Validators.required, Validators.maxLength(10)]],
      Password: [
        '',
        [
          Validators.required,
          Validators.maxLength(15),
          Validators.pattern('^(?=.*[A-Z])(?=.*\\d)[A-Za-z\\d]{8,}$')
        ]
      ]
    });
  }

  openpostModal() {
    console.log('karakum: ' + this.infoForm.value.FName);
  }

  onSubmit() {
    if (this.isRegistration) {
      if (this.infoForm.valid) {
        this.http.post(`${this.APIUrl}`, this.infoForm.value).subscribe({
          next: () => {
            console.log(`Posted: ${this.infoForm.value.Username}`);
            this.ngOnInit(); // Close the modal
          },
          error: (err) => {
            console.log(err);
            console.log('error here!');
          }
        });
      }
    } else {
    
      if (this.loginForm.valid) {
        console.log('Login form submitted:', this.loginForm.value);
        // You can implement your login logic here
      }
    }
  }

  toggleForm() {
    this.isRegistration = !this.isRegistration;
    this.infoForm.reset();
    this.loginForm.reset();
  }
}

interface items {
  ID: number;
  FName: string;
  Username: string;
  Password: string;
}
