import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { ToastService } from 'angular-toastify';
import { ActivatedRoute } from '@angular/router';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit {
  constructor(http: HttpClient, public toaster: ToastService, private route: ActivatedRoute) {
  }
  readonly APIUrl = 'https://localhost:7252/WeatherForecast';

  ngOnInit() {
    this.route.params.subscribe(params => {
      const ID = +params['id']; // Convert the parameter to a number
      console.log('ID from route:', ID);

      // Now you can use the ID in your home component as needed
    });
  }
  toggleChatbot(): void {
    const chatbotIframe = document.getElementById("chatbot-iframe") as HTMLElement;

    const currentDisplay = chatbotIframe.style.display || getComputedStyle(chatbotIframe).display;

    chatbotIframe.style.display = (currentDisplay === "none" || currentDisplay === "") ? "block" : "none";
  }

  public items: items[] = []; // Corrected variable name
  public showErrorToasterli() { this.toaster.success('User Logged in!'); }
}

interface items {
  ID: number;
  FName: string;
  Username: string;
  Password: string;
}
