import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { ToastService } from 'angular-toastify';
import { ActivatedRoute } from '@angular/router';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent implements OnInit{
  constructor(http: HttpClient,  public toaster: ToastService, private route: ActivatedRoute) { 
  }
  readonly APIUrl = 'https://localhost:7252/WeatherForecast';

  ngOnInit() {
    this.route.params.subscribe(params => {
      const ID = +params['id']; // Convert the parameter to a number
      console.log('ID from route:', ID);
  
      // Now you can use the ID in your home component as needed
    });
  }

  public items: items[] = []; // Corrected variable name
  public showErrorToasterli() { this.toaster.success('User Logged in!');}
}

interface items {
  ID: number;
  FName: string;
  Username: string;
  Password: string;
}

// Extend the Window interface to include the embeddedChatbotConfig property
interface CustomWindow extends Window {
  embeddedChatbotConfig?: EmbeddedChatbotConfig;
}

interface EmbeddedChatbotConfig {
  chatbotId: string;
  domain: string;
}

function setupEmbeddedChatbot(config: EmbeddedChatbotConfig): void {
  // Use the custom window interface
  (window as CustomWindow).embeddedChatbotConfig = config;

  const script = document.createElement('script');
  script.src = 'https://www.chatbase.co/embed.min.js';
  script.setAttribute('chatbotId', config.chatbotId);
  script.setAttribute('domain', config.domain);
  script.defer = true;

  document.head.appendChild(script);
}

// Example usage
const chatbotConfig: EmbeddedChatbotConfig = {
  chatbotId: 'o2gqZeOI8xSUmlrA8kdfr',
  domain: 'www.chatbase.co',
};

// Event listener for the button
const setupButton = document.getElementById('setupChatbotButton');
setupButton?.addEventListener('click', () => {
  setupEmbeddedChatbot(chatbotConfig);
});
