import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-fetch-data',
  templateUrl: './fetch-data.component.html'
})
export class FetchDataComponent implements OnInit {
  readonly APIUrl = 'https://localhost:7252/WeatherForecast';

  public items: items[] = [];
  public infoForm!: FormGroup;
  public accForm!: FormGroup;

  constructor(private http: HttpClient, private formBuilder: FormBuilder) {
    this.getItems();
  }

  ngOnInit() {
    this.infoForm = this.formBuilder.group({
      ID: [0],
      FName: ['', [Validators.required, Validators.maxLength(15)]],
      Username: ['', [Validators.required, Validators.maxLength(10)]],
      Password: ['', [Validators.required, Validators.maxLength(15),
                      Validators.pattern('^(?=.*[A-Za-z])(?=.*\\d)[A-Za-z\\d]{8,}$')]]
    });
    this.accForm = this.formBuilder.group({
      ID: [0],
      FName: ['', [Validators.maxLength(15)]],
      Username: ['', [Validators.maxLength(10)]],
      Password: ['', [Validators.maxLength(15),
                      Validators.pattern('^(?=.*[A-Za-z])(?=.*\\d)[A-Za-z\\d]{8,}$')]]
    });
  }

  getItems() {
    this.http.get<items[]>(this.APIUrl).subscribe({
      next: (value) => {
        this.items = value;
      },
      error: (err) => {
        console.log(err);
      },
    });
  }

  openEditModal(item: items) {
    this.accForm.patchValue(item);
  }

  openpostModal() {
    console.log('karakum: ' + this.infoForm.value.FName);
  }

  Posting() {
    if (this.infoForm.valid) {
      this.http.post(`${this.APIUrl}`, this.infoForm.value).subscribe({
        next: () => {
          console.log(`Posted: ${this.infoForm.value.Username}`);
          this.getItems();
          this.closeNewModal(); // Close the modal
        },
        error: (err) => { 
          console.log(err);
          console.log("error here!");
        },
      });
    }
  }

  updating() {
    if (this.accForm.valid) {
      this.http.put(`${this.APIUrl}`, this.accForm.value).subscribe({
        next: () => {
          console.log(`Updated by ID: ${this.accForm.value.ID}`);
          this.getItems();
          this.closeEditModal(); // Close the modal
        },
        error: (err) => {
          console.log(err);
          console.log("error here!");
        },
      });
    }
  }

  closeEditModal() {
    const modal = document.getElementById('exampleModal');
    if (modal) {
      modal.classList.remove('show');
      modal.style.display = 'none';
      document.body.classList.remove('modal-open');
      const modalBackdrop = document.querySelector('.modal-backdrop');
      if (modalBackdrop) {
        modalBackdrop.remove();
      }
    }
  }

  closeNewModal() {
    const modal = document.getElementById('newModal');
    if (modal) {
      modal.classList.remove('show');
      modal.style.display = 'none';
      document.body.classList.remove('modal-open');
      const modalBackdrop = document.querySelector('.modal-backdrop');
      if (modalBackdrop) {
        modalBackdrop.remove();
      }
    }
  }


  deleteInfo(ID: number) {
    this.http.delete(`${this.APIUrl}?ID=${ID}`).subscribe({
      next: () => {
        console.log(`Deleted by ID: ${ID}`);
        this.getItems()
      },
      error: (err) => {
        console.log(err);
      },
    })
  }

}


interface items {
  ID: number;
  FName: string;
  Username: string;
  Password: string;
}
