import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-counter-component',
  templateUrl: './counter.component.html'
})
export class CounterComponent implements OnInit {
  constructor(private http: HttpClient, private formBuilder: FormBuilder) {}

  readonly APIUrl = 'https://localhost:7252/WeatherForecast';

  public items: items[] = [];
  public infoForm!: FormGroup;
  public loginForm!: FormGroup;
  public isRegistration: boolean = true;

  ngOnInit() {
    this.infoForm = this.formBuilder.group({
      ID: [0],
      FName: ['', [Validators.required, Validators.maxLength(15)]],
      Username: ['', [Validators.required, Validators.maxLength(10)]],
      Password: [
        '',
        [
          Validators.required,
          Validators.maxLength(15),
          Validators.pattern('^(?=.*[A-Z])(?=.*\\d)[A-Za-z\\d]{8,}$')
        ]
      ]
    });

    this.loginForm = this.formBuilder.group({
      Username: ['', [Validators.required, Validators.maxLength(10)]],
      Password: [
        '',
        [
          Validators.required,
          Validators.maxLength(15),
          Validators.pattern('^(?=.*[A-Z])(?=.*\\d)[A-Za-z\\d]{8,}$')
        ]
      ]
    });
  }

  openpostModal() {
    console.log('karakum: ' + this.infoForm.value.FName);
  }

  onSubmit() {
    if (this.isRegistration) {
      if (this.infoForm.valid) {
        this.http.post(`${this.APIUrl}`, this.infoForm.value).subscribe({
          next: () => {
            console.log(`Posted: ${this.infoForm.value.Username}`);
            this.ngOnInit(); // Close the modal
          },
          error: (err) => {
            console.log(err);
            console.log('error here!');
          }
        });
      }
    } else {
      if (this.loginForm.valid) {
        // Handle login form submission
        console.log('Login form submitted:', this.loginForm.value);
        // You can implement your login logic here
      }
    }
  }

  toggleForm() {
    this.isRegistration = !this.isRegistration;
    this.infoForm.reset();
    this.loginForm.reset();
  }
}

interface items {
  ID: number;
  FName: string;
  Username: string;
  Password: string;
}
<div class="bg-image">
    <form class="d-flex justify-content-between" [formGroup]="isRegistration ? infoForm : loginForm" (ngSubmit)="onSubmit()">
        <div id="newModal" tabindex="-1">
            <div class="bg-info">
                <div style="width: 600px; height: 400px;"
                    class="mt-5 pt-5 d-flex bg-info-subtle position-relative d-flex justify-content-center bg-light border rounded-4 border-1 border-primary-subtle flex-column">
                    <!-- ... Modal header ... -->
                    <div class="d-flex justify-content-around">
                        <h4 class="m-3 d-flex justify-content-center">
                            {{ isRegistration ? 'Registration' : 'Login' }} section
                        </h4>
                        <div>
                            <ng-container *ngIf="isRegistration; else loginFields">
                                <!-- ... Registration form inputs ... -->
                            </ng-container>
                            <ng-template #loginFields>
                                <!-- ... Login form inputs ... -->
                            </ng-template>
                        </div>
                    </div>
                    <div class="modal-footer">
                        <button type="submit" style="width: 200px;"
                            class="btn btn-outline-info w-100 m-2 border-1 rounded text-dark shadow bg-body-tertiary">
                            {{ isRegistration ? 'Create' : 'Login' }}
                        </button>
                        <button type="button" [routerLink]="['/fetch-data']"
                            class="btn btn-transparent w-100 p-1 text-dark m-2">All accounts -></button>
                        <button type="button" (click)="toggleForm()"
                            class="btn btn-transparent w-100 p-1 text-dark m-2">
                            {{ isRegistration ? 'Switch to Login' : 'Switch to Registration' }}
                        </button>
                    </div>
                </div>
            </div>
        </div>
        <img src="https://static.vecteezy.com/system/resources/previews/009/205/381/non_2x/illustration-of-man-run-with-modern-laptop-captured-in-motion-hipster-surprised-bearded-web-developer-designer-or-programmer-with-laptop-modern-laptop-concept-continuous-line-design-style-vector.jpg"
            style="width: 600px; height: 440px; --bs-img-opacity: .5;" class="mt-5 img-thumbnail border-0"
            alt="...">
    </form>
</div>
<script src="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js"></script>
